mapper.select:
select /* BoardDao.getBoards */ board_idx as board_idx , board_name as board_name , board_desc as board_desc , use_yn as use_yn from board where 1=1
and board_name like concat('%',#{boardName},'%')
order by board_idx desc limit #{limit} offset #{offset}

mapper.insert:
insert /* BoardDao.createBoard */
  into board (
       board_name
     , board_desc
     , use_yn
) values (
       #{boardName}
     , #{boardDesc}
     , #{useYn}
)

mapper.update:
update /* BoardDao.updateBoard */
       board
   set board_name = #{boardName}
     , board_desc = #{boardDesc}
     , use_yn     = #{useYn}
 where board_idx = #{boardIdx}

mapper.delete:
delete /* BoardDao.deleteBoard */
  from board
 where board_idx = #{boardIdx}
